
public class Employee1 {
	public String name;
	public int age;
	public int salary;
	



	public Employee1(String name, int age, int salary) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.age = age;
		this.salary = salary;
	}

	public String getName() {
		return name;
	}

	

	public int getAge() {
		return age;
	}

	

	public int getSalary() {
		return salary;
	}

	

}
